mapper.select:
SELECT EMP_NO , EMP_NAME , EMAIL
FROM tbl_employee
WHERE EMP_NO = #{empNo}

mapper.update:
UPDATE tbl_send_email
SET TEMP_STATUS = 'N'
WHERE MAIL_NO = #{mailNo}

mapper.select:
SELECT *
FROM tbl_receive_email
WHERE TITLE = #{title}
AND SENDER_NAME = #{senderName}
AND STATUS = 'N'

mapper.update:
UPDATE tbl_receive_email
SET STATUS = 'Y'
WHERE RECEIVE_NO = #{mailNo}

UPDATE tbl_send_email
SET STATUS = 'Y'
WHERE MAIL_NO = #{mailNo}
