mapper.update:
UPDATE tbl_employee
SET DEPT_CODE = #{deptCode}
WHERE EMP_NO = #{empNo}

UPDATE tbl_employee
SET BRANCH_CODE = #{branchCode}
WHERE EMP_NO = #{empNo}

mapper.select:
SELECT E.EMP_NO, E.EMP_NAME, E.EMAIL, E.PHONE, E.ADDRESS, E.SALARY, E.ENROLL_DATE, E.RETIRE_DATE, E.WORK_STATUS, E.GENDER, E.JOB_CODE, J.JOB_NAME, E.DEPT_CODE, D.DEPT_NAME, B.BRANCH_CODE, B.BRANCH_NAME FROM tbl_employee E JOIN tbl_job J ON E.job_code = J.job_code JOIN tbl_department D ON E.dept_code = D.dept_code JOIN tbl_branch B ON E.branch_code = B.branch_code WHERE 1=1 AND WORK_STATUS = 'Y'
AND EMP_NAME = #{empName}
AND DEPT_NAME = #{deptName}
AND BRANCH_NAME = #{branchName}

SELECT *
FROM tbl_branch_order bo
         JOIN tbl_employee e ON bo.EMP_NO = e.EMP_NO
         JOIN tbl_branch b ON bo.BRANCH_CODE = b.BRANCH_CODE
WHERE e.EMP_NO = #{empNo}
  AND bo.ORDER_DATE BETWEEN #{startDate} AND #{endDate}

SELECT *
FROM tbl_department_order do
         JOIN tbl_employee e ON do.EMP_NO = e.EMP_NO
         JOIN tbl_department d ON do.DEPT_CODE = d.DEPT_CODE
WHERE e.EMP_NO = #{empNo}
  AND do.ORDER_DATE BETWEEN #{startDate} AND #{endDate}
