mapper.update:
UPDATE tbl_employee
SET DEPT_CODE = #{deptCode}
WHERE EMP_NO = #{empNo}

UPDATE tbl_employee
SET BRANCH_CODE = #{branchCode}
WHERE EMP_NO = #{empNo}

mapper.select:
SELECT

FROM tbl_employee
WHERE EMP_NAME = #{empName}

    AND DEPT_CODE = #{deptCode}


    AND BRANCH_CODE = #{branchCode}
