mapper.insert:
INSERT INTO tbl_schedule
(
    SCD_NAME
    , SCD_START_DAY
    , SCD_START_TIME
    , SCD_END_DAY
    , SCD_END_TIME
    , SCD_ALL_DAY
    , SCD_CONTENT
    , SCD_PLACE
    , CAL_NO
    , SCD_WRITER
)
VALUES
    (
        #{scdName}
    ,#{scdStartDay}
    ,#{scdStartTime}
    ,#{scdEndDay}
    ,#{scdEndTime}
    ,#{scdAllDay}
    ,#{scdContent}
    ,#{scdPlace}
    ,#{calNo}
    ,#{scdWriter}
    )

mapper.update:
UPDATE tbl_schedule
SET SCD_NAME = #{scdName}
  ,SCD_START_DAY = #{scdStartDay}
  ,SCD_START_TIME = #{scdStartTime}
  ,SCD_END_DAY = #{scdEndDay}
  ,SCD_END_TIME = #{scdEndTime}
  ,SCD_ALL_DAY = #{scdAllDay}
  ,SCD_CONTENT = #{scdContent}
  ,SCD_PLACE = #{scdPlace}
  ,CAL_NO = #{calNo}
  ,SCD_WRITER = #{scdWriter}
WHERE SCD_NO = #{scdNo}

mapper.select:
SELECT DISTINCT
    A.*
FROM tbl_calendar A
    LEFT JOIN tbl_calendar_mem B ON A.CAL_NO = B.CAL_NO
WHERE A.MADE_EMP_NO = #{empNo}
OR B.EMP_NO = #{empNo}

SELECT
    CAL_NO
FROM tbl_calendar
WHERE CAL_NAME = #{calName}
AND CAL_COLOR = #{calColor}
AND MADE_EMP_NO = #{madeEmpNo}

mapper.update:
UPDATE tbl_calendar
SET CAL_NAME = #{calName}
    ,CAL_COLOR = #{calColor}
    ,CAL_CONTENT = #{calContent}
WHERE CAL_NO = #{calNo}

mapper.delete:
DELETE FROM tbl_calendar_mem
WHERE CAL_NO = #{calNo}
  AND EMP_NO = #{empNo}

mapper.update:
UPDATE tbl_schedule_comment
SET COM_CONTENT = #{comContent}
WHERE COM_NO = #{comNo}
